import {Card, Col, Popconfirm, Tooltip} from 'antd';
import {ApiError} from 'components/common';
import PropTypes from 'prop-types';
import {useDeleteConcept} from 'queries/concept';
import {GET_CONCEPTS_LOAD_MORE} from 'queries/concept/constants';
import React, {useCallback} from 'react';
import {useQueryClient} from 'react-query';
import {useDispatch} from 'react-redux';
import {useNavigate, useParams} from 'react-router-dom';
import {deleteConceptRedux} from 'store/reducers/creative';
import {ShowToast} from 'utils/helpers/showToast.helpers';

import {DeleteOutlined, EditOutlined} from '@ant-design/icons';

import {FILE_DISPLAY_URL} from '../../../../constants';
import {RoutePaths} from '../../../../constants/route-paths';
import {InventoryFormats} from '../../../Container/constants';

const {Meta} = Card;
const noImage =
  'data:image/png;base64,';

function ConceptListItemAnt(props) {
  const {data} = props;
  const {name, advertiser_uuid, uuid: conceptId, fileUUID, fileType} = data;
  const {advertiserId} = useParams();
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const queryClient = useQueryClient();
  const {mutateAsync: deleteConceptRequest} = useDeleteConcept();

  const handleEdit = () => {
    navigate(`/${RoutePaths.CREATIVE}/${advertiser_uuid}/${conceptId}`);
  };

  const handleConfirmDelete = async () => {
    try {
      await deleteConceptRequest(conceptId);
      queryClient.invalidateQueries([GET_CONCEPTS_LOAD_MORE]);
      ShowToast.success('Delete Concept successfully!');
      dispatch(deleteConceptRedux(conceptId, advertiserId));
    } catch (error) {
      ShowToast.error(<ApiError apiError={error} />);
    }
  };
  const PreviewMediaComponent = useCallback(() => {
    if (fileType === InventoryFormats.BANNER) {
      return (
        <img
          className="concept-image"
          alt={name}
          src={fileUUID ? `${FILE_DISPLAY_URL}/${fileUUID}` : noImage}
        />
      );
    } else if (fileType === InventoryFormats.VIDEO) {
      return (
        <video
          controls
          className="concept-image"
          src={`${FILE_DISPLAY_URL}/${fileUUID}`}
        />
      );
    } else if (fileType === InventoryFormats.NATIVE_ADS) {
      return <img alt={''} src={noImage} />;
    }

    return <img alt={''} src={noImage} />;
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [fileUUID, fileType, conceptId]);

  return (
    <Col>
      <Card
        style={{width: 200}}
        cover={<PreviewMediaComponent />}
        actions={[
          <Tooltip placement="top" title="Edit this concept">
            <EditOutlined
              style={{color: '#545cd8'}}
              key="edit"
              onClick={handleEdit}
            />
          </Tooltip>,
          <Popconfirm
            placement="top"
            title="Are you sure to delete this concept?"
            onConfirm={handleConfirmDelete}
            okText="Yes"
            cancelText="No"
          >
            <Tooltip placement="top" title="Delete this concept">
              <DeleteOutlined style={{color: 'red'}} key="delete" />
            </Tooltip>
          </Popconfirm>
        ]}
      >
        <Meta
          title={
            <Tooltip title={name}>
              <span onClick={handleEdit} style={{cursor: 'pointer'}}>
                {name}
              </span>
            </Tooltip>
          }
        />
      </Card>
    </Col>
  );
}

ConceptListItemAnt.propTypes = {
  data: PropTypes.shape({
    name: PropTypes.string,
    id: PropTypes.string
  })
};
ConceptListItemAnt.defaultProps = {};

export default ConceptListItemAnt;
